package diameter.ro_r10;

import com.intellinet.diameter.*;


/**
 * The Initial-IMS-Charging-Identifier AVP (AVP code 2321) is of type UTF8String and holds the 
 * Initial IMS Charging Identifier (ICID) as generated by a IMS node for the initial SIP session created for IMS service continuity
 */
public class InitialIMSChargingIdentifier extends com.intellinet.diameter.DerivedAVP<String> {
    /**
     * Constructs a InitialIMSChargingIdentifier with the given value.
     *
     * @param value - the value of the AVP
     */
    public InitialIMSChargingIdentifier(String value) {
        super(new com.intellinet.diameter.UTF8StringData(value));
    }

    InitialIMSChargingIdentifier(com.intellinet.diameter.UTF8StringData data) {
        super(data);
    }
}
