package diameter.ro_r10;

import com.intellinet.diameter.*;


/**
 * PoC-Session-Id AVP is of type UTF8String. It uniquely identifies an end-to-end PoC session and
 * may be used for correlation between charging information generated by participating and controlling
 * PoC functions. This information is obtained from the "contact" header of the SIP message received
 * from the controlling PoC function.
 * Note:PoC-Session-Id may not be available in the initial charging interactions for the PoC session.
 */
public class PoCSessionId extends com.intellinet.diameter.DerivedAVP<String> {
    /**
     * Constructs a PoCSessionId with the given value.
     *
     * @param value - the value of the AVP
     */
    public PoCSessionId(String value) {
        super(new com.intellinet.diameter.UTF8StringData(value));
    }

    PoCSessionId(com.intellinet.diameter.UTF8StringData data) {
        super(data);
    }
}
