package diameter.ro_r10_transatel;

import com.intellinet.diameter.*;


/**
 * PDG-Charging-Id is of type Unsigned32 nd contains the charging identifier generated by the PDG
 * for the tunnel. Charging identifier is generated at tunnel establishment and transferred to
 * 3GPP AAA server.
 */
public class PDGChargingId extends com.intellinet.diameter.DerivedAVP<Integer> {
    /**
     * Constructs a PDGChargingId with the given value.
     *
     * @param value - the value of the AVP
     */
    public PDGChargingId(Integer value) {
        super(new com.intellinet.diameter.Unsigned32Data(value));
    }

    PDGChargingId(com.intellinet.diameter.Unsigned32Data data) {
        super(data);
    }
}
